schema:
{
  "type": "object",
  "title": "title",
  "properties": {
    "tmo_projectState": {
      "title": "项目状态",
      "type": "string",
      "description": "Project State",
      "enum": [
        "New",
        "Running",
        "Completed",
        "Archived",
        "Terminated",
        "Suspended",
        "Repetitive"
      ],
      "propertyOrder": 100
    },
    "tmo_projectPriority": {
      "type": "string",
      "title": "项目优先级",
      "description": "Project Priority",
      "enum": [
        "High",
        "Medium",
        "Low"
      ]
    },
		    "tmo_completionDate": {
      "type": "string",
      "title": "完成日期",
      "description": "Completion Date",
      "format": "date"
    },
    "tmo_projectAssessment": {
      "type": "string",
      "title": "项目评价",
      "description": "Project Assessment",
      "enum": [
        "General",
        "Simple",
        "Hard",
        "timeConsuming",
        "requiresAssistance",
        "tooDifficult"
      ]
    },
    "tmo_targetEndTime": {
      "title": "目标截止时间",
      "description": "Target End Time",
      "type": "string",
      "format": "date"
    },
    "tmo_actualStartTime": {
      "title": "实际开始时间",
      "description": "Actual Start Time",
      "type": "string",
      "format": "date"
    },
    "tmo_targetCompletion": {
      "title": "目标完成",
      "description": "Target Completion",
      "type": "boolean"
    },
    "tmo_targetStartTime": {
      "title": "目标开始时间",
      "description": "Target Start Time",
      "type": "string",
      "format": "date"
    },
    "tmo_actualEndTime": {
      "title": "实际结束时间",
      "description": "Actual End Time",
      "type": "string",
      "format": "date"
    },
    "tmo_importance_urgency": {
      "type": "string",
      "title": "任务重要与紧急",
      "enum": [
        "重要且紧急",
        "重要但不紧急",
        "不重要但紧急",
        "不重要也不紧急",
        "非常重要"
      ],
      "description": "Importance and Urgency"
    },
    "tmo_nextReviewIntervall": {
      "title": "下次回顾间隔",
      "description": "Next Review Intervall",
      "type": "string"
    },
    "super-tag": {
      "type": "string",
      "format": "date-time",
      "title": "Super Tag",
      "description": "超级表单"
    }
  },
  "required": [
    "tmo_projectPriority"
  ]
}
