schema:
{
  "type": "object",
  "properties": {
    "tmo_dueDate": {
      "title": "Due Date",
      "description": "截止日期",
      "type": "string",
      "format": "date"
    },
    "tmo_taskDescription": {
      "title": "Task Description",
      "description": "描述任务",
      "type": "string"
    },
    "tmo_targetEndTime": {
      "title": "Target End Time",
      "description": "目标截止时间",
      "type": "string",
      "format": "date"
    },
    "tmo_targetCompletion": {
      "title": "Target Completion",
      "description": "目标完成",
      "type": "boolean"
    },
    "tmo_actualStartTime": {
      "title": "Actual Start Time",
      "description": "实际开始时间",
      "type": "string",
      "format": "date"
    },
    "tmo_dependency": {
      "title": "Dependency",
      "description": "依赖",
      "type": "string"
    },
    "tmo_nextReviewIntervall": {
      "title": "Next Review Intervall",
      "description": "下次回顾间隔",
      "type": "string"
    },
    "tmo_actualCompletion": {
      "type": "boolean",
      "description": "实际完成",
      "title": "Actual Completion"
    },
    "tmo_taskState": {
      "title": "Task State",
      "type": "string",
      "description": "任务的状态",
      "enum": [
        "New",
        "Running",
        "Completed",
        "Archived",
        "Terminated",
        "Suspended",
        "Repetitive"
      ],
      "propertyOrder": 100
    },
    "tmo_lastReviewDate": {
      "title": "Last Review Date",
      "description": "上次检查时间",
      "type": "string",
      "format": "date"
    },
    "tmo_repeatInterval": {
      "title": "Repeat Interval",
      "description": "间隔次数",
      "type": "number"
    },
    "tmo_numberOfCompletions": {
      "title": "Number Of Completions",
      "description": "完成次数",
      "type": "string"
    },
    "tmo_targetStartTime": {
      "title": "Target Start Time",
      "description": "目标开始时间",
      "type": "string",
      "format": "date"
    },
    "tmo_actualEndTime": {
      "title": "Actual End Time",
      "description": "实际结束时间",
      "type": "string",
      "format": "date"
    },
    "tmo_priority": {
      "title": "Priority",
      "description": "优先级",
      "type": "string",
      "enum": [
        "High",
        "Medium",
        "Low"
      ],
      "propertyOrder": 101
    },
    "tmo_importance_urgency": {
      "type": "string",
      "title": "Importance and Urgency",
      "enum": [
        "重要且紧急",
        "重要但不紧急",
        "不重要但紧急",
        "不重要也不紧急",
        "非常重要"
      ],
      "description": "任务重要与紧急"
    }
  },
  "required": [
    "tmo_taskState"
  ],
  "defaultProperties": [
    "tmo_taskState"
  ]
}
