schema:
{
  "type": "object",
  "properties": {
    "tmo_dueDate": {
      "title": "截止日期",
      "description": "Due Date",
      "type": "string",
      "format": "date"
    },
    "tmo_taskDescription": {
      "title": "描述任务",
      "description": "Task Description",
      "type": "string"
    },
    "tmo_dependency": {
      "title": "依赖或前置",
      "description": "Dependency",
      "type": "string"
    },
    "tmo_actualCompletion": {
      "type": "boolean",
      "description": "Actual Completion",
      "title": "实际完成"
    },
    "tmo_taskState": {
      "title": "任务状态",
      "type": "string",
      "description": "Task State",
      "enum": [
        "New",
        "Running",
        "Completed",
        "Archived",
        "Terminated",
        "Suspended",
        "Repetitive"
      ],
      "propertyOrder": 100
    },
    "tmo_repeatInterval": {
      "title": "间隔次数",
      "description": "Repeat Interval",
      "type": "number"
    },
    "tmo_numberOfCompletions": {
      "title": "完成次数",
      "description": "Number Of Completions",
      "type": "string"
    },
    "tmo_priority": {
      "title": "优先级",
      "description": "Priority",
      "type": "string",
      "enum": [
        "High",
        "Medium",
        "Low"
      ],
      "propertyOrder": 101
    },
    "super-tag": {
      "type": "string",
      "format": "date-time",
      "title": "Super Tag",
      "description": "超级表单"
    },
    "tmo_timeSpent": {
      "type": "string",
      "description": "时间花费不能为空",
      "title": "时间花费"
    }
  },
  "required": [
    "tmo_taskState",
    "tmo_timeSpent",
    "super-tag",
    "tmo_timeSpent"
  ],
  "defaultProperties": [
    "tmo_taskState"
  ]
}
